import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux';

import './stylesheet.scss'

import MenuIcon from './menuIcon.png'

const Menu = props => {
    const [open, setOpen] = useState(0);

    const toggleOpen = () => {
        setOpen(!open);
    }

    return <>
        <div id="Menu">
            <div id="logo">
                <Link to="/" style={{color: "black"}}> <h1>SRSI</h1> </Link>
            </div>
            <div id="Menus">
                <img id="MenuButton" alt="Menu Button" src={MenuIcon} onClick={toggleOpen} />
            </div>
        </div>
        <div id="NavMenu" className={open ? "NavMenuOpened" : "NavMenuClosed"}>
            <br/> <br/>
            <Link to="/" onClick={toggleOpen}> <span className="MenuLink">Pagina PrincipalÄƒ</span> </Link> <br/> <br/>
            <Link to="/despre-noi" onClick={toggleOpen}> <span className="MenuLink">Despre Noi</span> </Link> <br/> <br/>
            <Link to="/membri" onClick={toggleOpen}> <span className="MenuLink">Membri</span> </Link> <br/> <br/>
            <Link to="/evenimente" onClick={toggleOpen}> <span className="MenuLink">Evenimente</span> </Link> <br/> <br/>
            <Link to="/contact" onClick={toggleOpen}> <span className="MenuLink">Contact</span> </Link> <br/> <br/>
        </div>
    </>;
};

const mapStateToProps = state => {
    return {
        menuOn: state.menuOn
    }
}

const mapDispatchToProps = dispatch => {
    return {
        dispatch
    };
}

export default connect(
    mapStateToProps,
    mapDispatchToProps,
    Menu
);
