import { createStore, compose } from 'redux'


const initialState = {
    menuOn: false,
}

const reducer = (state = initialState, action) => {
    console.log(state);
    switch (action.type) {
        case 'MENU_TOGGLE':
            return {
                menuOn: !state.menuOn,
                ...state
            }
        case 'MENU_TURN_ON':
            return {
                menuOn: true,
                ...state
            }
        case 'MENU_TURN_OFF':
            return {
                menuOn: false,
                ...state
            }
        default:
            return state;
    }
}

const store = createStore(reducer);

export default store;
