import React, { useRef, useEffect } from 'react';
import TermBlink from '../../components/termblink'

const Prezentare = props => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        context.font = "16px JetBrainsMono, Courier, monospace";

        let frameCount = 0;

        const frame = () => {
            context.fillStyle = '#fff';
            context.fillRect(0, 0, 10000, 10000);

            context.fillStyle = '#888';
            const d = new Date();
            let t = d.getTime() / 3000;

            for (let i = 0; i < digits.length; ++i)
                context.fillText(digits[i].val, digits[i].px + cellSide * Math.sin(digits[i].a1 * t), digits[i].py + cellSide * Math.cos(digits[i].b1 * t));


            window.requestAnimationFrame(frame);
        }
        window.requestAnimationFrame(frame);
    });

    return (
        <div id="prezentare">
            <h1><TermBlink>Despre Noi</TermBlink></h1>
            <div id="prezentare-screen">
                <canvas ref={canvasRef} id="VennDiagramCanvas" width="600px" height="600px" />
            </div>
        </div>
    );
};

export default Prezentare;
