import React, { useRef, useEffect } from 'react';
import './background.scss';

const Background = props => {
    const canvasRef = useRef(null);
    const cellsX = Math.floor(window.innerWidth / 32);
    const cellsY = Math.floor(window.innerHeight / 32);
    const cellSide = 32;

    let digits = [];
    for (let i = 0; i < cellsX; ++i)
        for (let j = 0; j < cellsY; ++j) {
            digits.push({
                px: i * cellSide + Math.random() * cellSide,
                py: j * cellSide + Math.random() * cellSide,
                a1: Math.random(),
                a2: Math.random(),
                b1: Math.random(),
                b2: Math.random(),
                d1: Math.random(),
                d2: Math.random(),
                val: Math.random() < 0.5 ? '0' : '1'
            });
        }

    console.log(digits);

    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        context.font = "16px Arial";

        const d = new Date();

        window.requestAnimationFrame(() => {
            context.fillStyle = '#000';
            let t = d.getTime();

            for (let i = 0; i < digits.length; ++i)
                context.fillText(digits[i].val, digits[i].px + Math.sin(digits[i].a), digits[i].py)
        });
    });


    return <canvas ref={canvasRef} {...props} />;
}

export default Background;
