import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux';

import './stylesheet.scss'

import MenuIcon from './menuIcon.png'

const Menu = props => {
    const [open, setOpen] = useState(0);

    const toggleMenu = () => {
        props.dispatch({
            type: 'MENU_TOGGLE'
        });
    }

    const shutMenu = () => {
        props.dispatch({
            type: 'MENU_TURN_OFF'
        });
    }

    return <>
        <div id="Menu">
            <div id="logo">
                <Link to="/" onClick={shutMenu} style={{color: "black"}}> <h1>SRSI</h1> </Link>
            </div>
            <div id="Menus">
                <img id="MenuButton" alt="Menu Button" src={MenuIcon} onClick={toggleMenu} />
            </div>
        </div>
        <div id="NavMenu" className={props.menuOn ? "NavMenuOpened" : "NavMenuClosed"}>
            <br/> <br/>
            <Link to="/" onClick={toggleMenu}> <span className="MenuLink">Pagina Principală</span> </Link> <br/> <br/>
            <Link to="/despre-noi" onClick={toggleMenu}> <span className="MenuLink">Despre Noi</span> </Link> <br/> <br/>
            <Link to="/activitati" onClick={toggleMenu}> <span className="MenuLink">Activități</span> </Link> <br/> <br/>
            <Link to="/membri" onClick={toggleMenu}> <span className="MenuLink">Membri</span> </Link> <br/> <br/>
            <Link to="/contact" onClick={toggleMenu}> <span className="MenuLink">Contact</span> </Link> <br/> <br/>
        </div>
    </>;
};

const mapStateToProps = state => {
    return {
        menuOn: state.menuOn
    }
}

const mapDispatchToProps = dispatch => {
    return {
        dispatch
    };
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Menu);
