import React, { useRef, useEffect } from 'react';
import './background.scss';

const Background = props => {
    const canvasRef = useRef(null);
    const cellsX = Math.floor(window.innerWidth / 32);
    const cellsY = Math.floor(window.innerHeight / 32);
    const cellSide = 64;

    let digits = [];
    for (let i = 0; i < cellsX; ++i)
        for (let j = 0; j < cellsY; ++j) {
            digits.push({
                px: i * cellSide + Math.random() * cellSide,
                py: j * cellSide + Math.random() * cellSide,
                a1: 0.5 + 0.5 * Math.random(),
                a2: 0.5 + 0.5 * Math.random(),
                b1: 0.5 + 0.5 * Math.random(),
                b2: 0.5 + 0.5 * Math.random(),
                d1: 0.5 + 0.5 * Math.random(),
                d2: 0.5 + 0.5 * Math.random(),
                val: Math.random() < 0.5 ? '0' : '1'
            });
        }

    console.log(digits);

    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        context.font = "16px Arial";

        const frame = () => {
            context.fillStyle = '#fff';
            context.fillRect(0, 0, 10000, 10000);

            context.fillStyle = '#888';
            const d = new Date();
            let t = d.getTime();

            console.log(t);
            for (let i = 0; i < digits.length; ++i)
                context.fillText(digits[i].val, digits[i].px + cellSide * (Math.sin(digits[i].a1 * t / 1000) + Math.sin(digits[i].a2 * t / 1000)), digits[i].py + cellSide * (Math.cos(digits[i].b1 * t / 1000) + Math.cos(digits[i].b1 * t / 1000)));


            window.requestAnimationFrame(frame);
        }
        window.requestAnimationFrame(frame);
    });


    return <canvas ref={canvasRef} {...props} width={window.innerWidth} height={window.innerHeight} />;
}

export default Background;
