import './background.scss';

class Background extends React.Component {
    constructor(props) {
        super(props);

        this.myCanvas = React.createRef();
        this.state = {
            width: window.innerWidth,
            height: window.innerHeight,
            digits: []
        }

        this.initDigits();
        window.onresize((event) => {
            if (Math.abs(window.innerWidth - this.state.width) > 32 || Math.abs(window.innerHeight - this.state.height) > 32) {
                this.setState({
                    width: window.innerWidth,
                    height: window.innerHeight
                });
                this.initDigits();
            }
        });

        this.drawDigits();
    }

    makeDigit(px, py) {
        return {
            a1: 0.5 - Math.random(),
            a2: 0.5 - Math.random(),
            b1: 0.5 - Math.random(),
            b2: 0.5 - Math.random(),
            da1: 0.5 - Math.random(),
            da2: 0.5 - Math.random(),
            db1: 0.5 - Math.random(),
            db2: 0.5 - Math.random(),
            ix: px,
            iy: py,
            digit: (Math.random() < 0.5 ? '0' : '1')
        };
    }

    initDigits() {
        const cellSide = 32;

        const cellsX = Math.floor(this.state.height / cellSide);
        const cellsY = Math.floor(this.state.width / cellSide);

        var digits = [];

        for (var i = 0; i < cellsX; ++i)
        for (var j = 0; j < cellsY; ++j)
