import React, { useRef, useEffect } from 'react'
import TermBlink from '../../components/termblink'

import './prezentare.scss'


const Prezentare = props => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.font = "16px JetBrainsMono, Courier, monospace";

        let frameCount = 0;

        const frame = () => {
            ctx.fillStyle = '#fff';
            ctx.fillRect(0, 0, 10000, 10000);

            ctx.beginPath();
            ctx.arc(100, 1000, 50, 0, 2 * Math.PI);
            ctx.stroke();

            window.requestAnimationFrame(frame);
        }
        window.requestAnimationFrame(frame);
    });

    return (
        <div id="prezentare">
            <h1><TermBlink>Despre Noi</TermBlink></h1>
            <div id="prezentare-screen">
                <canvas ref={canvasRef} id="VennDiagramCanvas" width="600px" height="600px" />
            </div>
        </div>
    );
};

export default Prezentare;
