import React, { useRef, useEffect } from 'react';
import './stylesheet.scss';
import {Link} from 'react-router-dom'

import DefaultAvatar from './defaultAvatar.png'

const PersonalCard = props => {
    const avatar = props.avatar ? props.avatar : DefaultAvatar;
    const name = props.name;
    const size = (props.small ? 'small' : 'large');

    console.assert(name);

    return (
        <div className="PersonalCard">
            <div className="AvatarContainer">
                <Link to={"/membri/" + props.name}><img className="Avatar" alt={props.name} src={avatar} /></Link>
            </div>
            <div className="PersonalTldr">
                {props.name ? <> <Link to={"/membri/" + props.name}> <h2><u> { props.name } </u></h2> </Link> <br/> </> : null }
                {props.institution ? <><span> { props.institution } </span> <
                    br/> <br/> </> : null }
                {props.role ? <><span> { props.role } </span> <br/> <br/> </> : null }
            </div>
        </div>
    );
}

export default PersonalCard;
