import React, { useRef, useEffect } from 'react'
import TermBlink from '../../components/termblink'

import './prezentare.scss'


const Prezentare = props => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.font = "16px JetBrainsMono, Courier, monospace";

        const frameLim = 60 * 5;
        let frameCount = 0;

        const frame = () => {
            ctx.fillStyle = '#fff';
            ctx.fillRect(0, 0, 10000, 10000);

            const progress = 2 - (frameCount / frameLim);

            const drawCircle = (x, y, color, text) => {
                ctx.beginPath();
                ctx.arc(x, y, 150, 0, 2 * Math.PI);
                ctx.strokeStyle = 'rgba(0, 0, 0, 0)';
                ctx.fillStyle = color;
                ctx.fill();
                ctx.stroke();
            }

            drawCircle(300, 300 + progress * 150, "rgba(245, 255, 0, 0.5)", 0);
            drawCircle(300 - 150 * Math.cos(Math.PI / 6) * progress, 300 - 150 * Math.sin(Math.PI / 6) * progress, "rgba(255, 0, 0, 0.5)", 0);
            drawCircle(300 + 150 * Math.cos(Math.PI / 6) * progress, 300 - 150 * Math.sin(Math.PI / 6) * progress, "rgba(0, 0, 255, 0.5)", 0);

            if (frameCount < frameLim / 3) {
                frameCount+= 1;
                window.requestAnimationFrame(frame);
            }
        }
        window.requestAnimationFrame(frame);
    });

    return (
        <div id="prezentare">
            <h1><TermBlink>Despre Noi</TermBlink></h1>
            <div id="prezentare-screen">
                <canvas ref={canvasRef} id="VennDiagramCanvas" width="600px" height="600px" />
            </div>
        </div>
    );
};

export default Prezentare;
