import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'
import './stylesheet.scss'

import MenuIcon from './menuIcon.png'

const Menu = props => {
    const [open, setOpen] = useState(0);

    useEffect(() => {
        setOpen(false);
    })

    return <>
        <div id="Menu">
            <div id="logo">
                <h1>SRSI</h1>
            </div>
            <div id="Menus">
                <img id="MenuButton" alt="Menu Button" src={MenuIcon} width="50px"/>
            </div>
        </div>
        <div id="NavMenu" className={open ? "opened" : "closed"}>
            <Link to="/"> Pagina Principală </Link> <br/>
            <Link to="/despre-noi"> Despre Noi </Link> <br/>
            <Link to="/evenimente"> Evenimente </Link> <br/>
            <Link to="/articole"> Articole </Link> <br/>
            <Link to="/contact"> Contact </Link> <br/>
            <Link to="/noutati"> Noutăți </Link> <br/>
        </div>
    </>;
};

export default Menu;
